import React, { Component } from "react";
import CardPeliculas from "../cards/CardPeliculas";
import Carrousel from "./Carrousel";
import styles from "./Movies.module.css";
const api=" https://api.themoviedb.org/3/search/movie?api_key=b5138e06a3a9125b8c326498bbeae997&";
class Movies extends Component{
    constructor(){
        super();
        this.state={
            datos:[],
            buscar:"",
            error:""
        }
    }
    async componentDidMount(){
        const peliculasPag= await fetch(`${api}query=joker`)
        const peliculasJson= await peliculasPag.json()
        const peliculas= peliculasJson.results;
        this.setState({datos:peliculas});
    }

    async buscadorPelis(e){
        e.preventDefault();
        if(!this.state.buscar){
        return this.setState({error:"Escribe algo por favor"})}

        const peliculasPag= await fetch(`${api}query=${this.state.buscar}`)
        const peliculasJson= await peliculasPag.json()
        const peliculas= peliculasJson.results;

        if(peliculas.length<=0){
        return this.setState({error:"Actualmente no tenemos ese titulo,intenta con otro"})}
        this.setState({datos:peliculas, error:""})
    }
   
    render(){
        return( 
        <div className={styles.container}>
            <div className={styles.containerSun}>
                <div className={styles.divCarousel}>
                {this.state.datos.map((movie, i)=>{
                    return <CardPeliculas movies={movie} key={i}/>})}
                </div>
                <div className={styles.divForm}>
                    <form className={styles.form} onSubmit={(e)=> this.buscadorPelis(e) } >
                        <input className={styles.input} onChange={e=>this.setState({buscar:e.target.value})}
                        type="text"  
                        placeholder="Escribe una peli">      
                        </input> 
                    </form>  
                    <p className={styles.error}>{this.state.error? this.state.error : " "} </p>
                </div>
                
                <div className={styles.movies}>
                    {this.state.datos.map((movie, i)=>{
                    return <CardPeliculas movies={movie} key={i}/>})}
                </div>
                
            </div>
            
          {/*  <div class={styles.wrapper}>
                    
                    <div class={styles.sidebar}>
                        <h2>1pro</h2>
                        <h2>1pro</h2>
                        <h2>1pro</h2>

                    </div>
                    </div>*/}
        </div>
        )
    }
}
export default Movies;
