import React, {useState } from "react";
import {Carousel} from 'react-bootstrap';

import styles from "./Carrousel.module.css"
function Carrousel(props) {
  const [path, setPath]=useState("")
props.datos.map((movie,i)=>{
  setPath(movie[0].poster_path)
})
console.log(props.datos)
  const [index, setIndex] = useState(0);

  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
  };
  
  return (
  <div className={styles.container}>
    <div className={styles.containerTitle}>
      <h2 className={styles.title}> My Projects </h2>
    </div>
  
    <div className={styles.containerCarousel}>
      <Carousel className={styles.carousel} activeIndex={index} interval={null} onSelect={handleSelect}>
        <Carousel.Item  className={styles.images}>
          <div className={styles.divImagen}>
            <img
              id={styles.imagen1}
              className="d-block w-100"
              src={"http://image.tmdb.org/t/p/w400/" + path}/>
          </div>
  
          <Carousel.Caption>
            <button className={styles.buton}>Weather App</button>
          </Carousel.Caption>
        </Carousel.Item>
        <Carousel.Item  className={styles.images}>
          <div className={styles.divImagen}>
            <img
              id={styles.imagen1}
              className="d-block w-100"
              src=""
              alt="Weather App"
            />
          </div>
  
          <Carousel.Caption>
            <button className={styles.buton}>Weather App</button>
          </Carousel.Caption>
        </Carousel.Item>
  
        <Carousel.Item className={styles.images2}>
          <img 
            id={styles.imagen2}
  
            className="d-block w-100"
            src=""

            alt="To Do App"
          />
  
          <Carousel.Caption >
           <button className={styles.butonToDo}>To Do App</button>
          </Carousel.Caption>
        </Carousel.Item>
  
       
      </Carousel>
    </div>
  </div>
    );
  }
export default Carrousel;
