import React, { Component } from "react";
/*   */

class Card extends Component{
    constructor(props){
        super(props);
        this.state={
            id:1,
        }

        
    }
    render(){
        return (
            <div>
              <img  className="img" src={"http://image.tmdb.org/t/p/w200/"+ this.props.movies.poster_path} />


                <p className="p" hidden={true} id={this.props.movies.id}>{this.props.movies.overview} </p>
              
                <button >Ver Overview</button>
            </div>
        )
    }
}
export default Card;
