import React, { Component } from "react";

var tiposGenero={
    "genero":
    [
        {"Action":28},
       {"Adventure": 12},
        {"Animation": 16},
        {"Comedy": 35},
        {"Crime":80},
        {"Documentary":99},
        {"Drama":18},
        {"Family":10751},
        {"Fantasy":14},
        {"History":36},
        {"Horror":27},
        {"Music": 10402},
        {"Mystery":9648},
        {"Romance": 10749},
        {"Science Fiction": 878},
        {"TV Movie": 10770},
        {"Thriller":53},
        {"War": 10752},
        {"Western": 37}
    ]
   
}

class Card extends Component{
    constructor(props){
        super(props);
    }
    render(){
        return (
            <div>
                {
                    tiposGenero.map(m=>{
                        console.log(m)
                    })
                }
                <h2>{this.props.movies.title} </h2>
              <img onClick={()=>
                    { 
                    let id= document.getElementById(this.props.movies.id);
                     if(id.hidden===true){
                         id.hidden=false;
                     }else if(id.hidden===false){
                         id.hidden=true;
                     }
                    } 
                }  className="img" src={"http://image.tmdb.org/t/p/w200/"+ this.props.movies.poster_path} alt={this.props.movies.title} />
                <p className="p" hidden={true} id={this.props.movies.id}>{this.props.movies.overview} </p>
            </div>
        )
    }
}
export default Card;
