import React, { Component } from "react";
import CardSeries from "../cards/CardSeries";
const api=" https://api.themoviedb.org/3/search/movie?api_key=b5138e06a3a9125b8c326498bbeae997&";
class Serie extends Component{
    constructor(){
        super();
        this.state={
            datos:[],
            buscar:"",
            error:""
        }

    }
async componentDidMount(){
    const seriePag= await fetch(`${api}query=batman`)
    const serieJson= await seriePag.json()
    const serie= serieJson.results;
    this.setState({datos:serie});

}
async buscadorPelis(e){
    e.preventDefault();
    if(!this.state.buscar){
        return this.setState({error:"Escribe algo por favor"})
    }

    const seriePag= await fetch(`${api}query=${this.state.buscar}`)
    const serieJson= await seriePag.json()
    const serie= peliculasJson.results;

    if(peliculas.length<=0){
       return this.setState({error:"Actualmente no tenemos ese titulo,intenta con otro"})
    }


    this.setState({datos:peliculas, error:""})

}
   
    render(){
        return( 
        <div className="preguntas">
            <div>
                <div>
                    <form onSubmit={(e)=> this.buscadorPelis(e) } >
                        <input onChange={e=>this.setState({buscar:e.target.value})}
                        type="text"  
                        placeholder="Escribe una serie">      
                        </input> 
                    </form>  
                    <p>{this.state.error? this.state.error : " "} </p>
                 
                </div>
            </div>
            <div className="peliculas">
                {
                this.state.datos.map((movie, i)=>{
                  return <CardSeries movies={movie} key={i}/>
                })
                }
            </div>
        </div>
        )
    }
}

export default Serie;
