import React, { Component } from "react";
/*   */

class Card extends Component{
    constructor(props){
        super(props);
        this.state={
            id:1,
        }

        
    }
    render(){
        return (
            <div>
                <h2>{} </h2>
              <img onClick={()=>
                    { 
                    let id= document.getElementById(this.props.movies.id);
                     if(id.hidden==true){
                         id.hidden=false;
                     }else if(id.hidden==false){
                         id.hidden=true;
                     }
                    } 
                }  className="img" src={"http://image.tmdb.org/t/p/w200/"+ this.props.movies.poster_path} />
                <p className="p" hidden={true} id={this.props.movies.id}>{this.props.movies.overview} </p>
            </div>
        )
    }
}
export default Card;
