import React, { Component } from "react";

var tiposGenero={
    "genero":
    {
        "accion":28,
        "adventure": 12,
        "animation": 16,
        "Comedy": " "
    }
   
}

class Card extends Component{
    constructor(props){
        super(props);
    }
    render(){
        return (
            <div>
                {
                this.props.movies.genre_ids.map(genero=>{
                    console.log(genero)
                })
                }
                <h2>{this.props.movies.title} </h2>
              <img onClick={()=>
                    { 
                    let id= document.getElementById(this.props.movies.id);
                     if(id.hidden===true){
                         id.hidden=false;
                     }else if(id.hidden===false){
                         id.hidden=true;
                     }
                    } 
                }  className="img" src={"http://image.tmdb.org/t/p/w200/"+ this.props.movies.poster_path} alt={this.props.movies.title} />
                <p className="p" hidden={true} id={this.props.movies.id}>{this.props.movies.overview} </p>
            </div>
        )
    }
}
export default Card;
