import React, { Component } from "react";
import Card from "../cards/Card"
const api=" https://api.themoviedb.org/3/search/movie?api_key=b5138e06a3a9125b8c326498bbeae997&";
class Movies extends Component{
    constructor(){
        super();
        this.state={
            datos:[],
            buscar:"",
            error:"",
            generos:[]
        }

    }
async componentDidMount(){
    const peliculasPag= await fetch(`${api}query=joker`)
    const peliculasJson= await peliculasPag.json()
    const peliculas= peliculasJson.results;
    this.setState({datos:peliculas});

}
async buscadorPelis(e){
    e.preventDefault();
    if(!this.state.buscar){
        return this.setState({error:"Escribe algo por favor"})
    }

    const peliculasPag= await fetch(`${api}query=${this.state.buscar}`)
    const peliculasJson= await peliculasPag.json()
    const peliculas= peliculasJson.results;
    this.setState({datos:peliculas})

    const generoApi= await fetch("https://api.themoviedb.org/3/genre/movie/list?api_key=b5138e06a3a9125b8c326498bbeae997&language=en-US")
    const generoJson= await generoApi.json()
    const genero= generoJson.genres;
    this.setState({generos:genero})
}
   
    render(){
        return( 
        <div className="preguntas">
            <div>
                <div>
                    <form onSubmit={(e)=> this.buscadorPelis(e) } >
                        <input onChange={e=>this.setState({buscar:e.target.value})}
                        type="text"  
                        placeholder="Escribe una peli">      
                        </input> 
                    </form>  
                    <p>{this.state.error} </p>
                 
                </div>
            </div>
            <div className="peliculas">
                {
                this.state.datos.map((movie, i)=>{
                  return <Card movies={movie} key={i}/>
                })
                }
                {this.state.generos.map(genero=>{
                    return <h2>{console.log()}</h2> 
                })}
            </div>
        </div>
        )
    }
}

export default Movies;
